import React, { useState, useEffect } from "react";
import './App.css';

import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import { teal, red } from '@material-ui/core/colors';

import {MenuBar} from './Components/core/menuBar/MenuBar';
import {MainPage} from './Components/pages/mainPage/MainPage';
import {Clients} from './Components/pages/clients/Clients';


export function App() {

  const theme = createMuiTheme({
    palette: {
      primary: {
        light: '#757ce8',
        main: teal[500],
        dark: '#002884',
        contrastText: '#fff',
      },
      secondary: {
        light: '#ff7961',
        main: red[500],
        dark: '#ba000d',
        contrastText: '#000',
      },
      page:{
        light:"white",
      },
    },
  });

  const [pageSelected, setpageSelected] = useState("MAINPAGE");
  const [device, setDevice] = useState("MONITOR");


  useEffect((err, data) => {
    var mql = window.matchMedia("(max-width: 480px)");
    console.log("mql", mql);

    if (mql.matches)
      setDevice("MOBILE");

  }, []);


  function onChangePage(pageName) {
    setpageSelected(pageName);
  }


  return(
    <ThemeProvider theme={theme}>
      <div id="App">
        <div style={{ display: device == "MONITOR" ? "flex" : "none"}}>

          {device == "MONITOR" ?
            <MenuBar onClick={(pageName) => onChangePage(pageName)}/>
          : undefined}

          <div style={{ width: "calc(100vw - 60px)"}}>

            {pageSelected === "MAINPAGE" ?
              <>
                <MainPage />
              </>
            :
              (pageSelected === "CLIENTS" ?
                <Clients />
              : undefined)
            }

          </div>
        </div>
      </div> 
    </ThemeProvider>
  )
}
