const helper = {

    displayCEP: function (_v) {
        if (!_v) return "";

        var v = _v + "";

        v = v.replace(/\D/g, "").substr(0, 8);
        v = v.replace(/(\d{5})(\d)/, "$1-$2");

        return v;
    },
    /*export class C_TextFieldCEP extends React.Component {

  getLabel(parameterLabel) {
    if (parameterLabel) return parameterLabel;
    return 'CEP';
  }

  getZip(zip) {
    if (zip || this.props.value) {
      helper.beGet('/api/patient/get_zip', {
        zip: zip || this.props.value
      }, false, false, true, this, (err, result) => {
        this.props.search(result)
      });
    }
  }

  changeValue(res) {
    this.props.onChange(res);
    if (this.props.search) {
      if (res.target.value.length == 9)
        this.getZip(res.target.value);
    }
  }

  render() {
    return (
      <C_TextFieldType id={this.props.id}
        processValue={helper.displayCEP}
        onChange={res => this.changeValue(res)}
        label={this.getLabel(this.props.label)}
        name={this.props.name}
        disabled={this.props.disabled}
        placeholder={this.props.placeholder}
        className={this.props.className}
        fullWidth={this.props.fullWidth}
        inputStyle={this.props.inputStyle}
        value={helper.displayCEP(this.props.value)}
        leftIcon={this.props.icon}
        type={this.props.type}
        error={this.props.errorText && this.props.errorText.length > 0}
        errorText={this.props.errorText}
        onFocus={this.props.onFocus}
        onBlur={this.props.onBlur}
        style={this.props.style}
        rightIcon={
          this.props.search ?
            <C_ButtonIcon
              icon="search"
              onClick={(e) => {
                this.getZip();
              }}
              style={{ margin: 0 }}
            /> : undefined
        }
      />
    );
  }
}
*/

