import React, { useState, useEffect } from 'react';

import "../CoreStyle.css";
import "./ComponentsStyle.css";
import { Button } from "@react-md/button";
import { FormMessage, TextField, Select} from "@react-md/form";
import { FontIcon, TextIconSpacing } from "@react-md/icon";
import { FaCarSide } from 'react-icons/fa';


export function C_Icon(props) {
   
    var style = props.style ? props.style : {};

    return (
        <Button 
            onClick={props.onClick}
            icon={props.icon} 
            aria-label="More Info" 
            buttonType="icon"
            theme={props.theme ? props.theme : props.primary ? "primary" : props.secondary ? "secondary" : "primary"}
            style={style}
            >
            <FontIcon>{props.icon}</FontIcon>
        </Button>
    )
}

export function C_ButtonIcon(props) {
   
    var style = props.style ? props.style : {};

    return (
        <div>
            <Button 
                onClick={props.onClick}
                theme={props.theme ? props.theme : props.primary ? "primary" : props.secondary ? "secondary" : "primary"}
                themeType={props.themeType ? props.themeType : "contained"}
                style={style}
                >
                {props.icon ? 
                    <TextIconSpacing icon={<FontIcon>{props.icon}</FontIcon>}>
                        {props.label}
                    </TextIconSpacing>
                :
                    <>{props.label}</>
                }
            </Button>
        </div>
    )
}

export function C_Modal(props) {


    function closeModal(){
        if (props.onClose)
            props.onClose();
    }

    return (
        <>
            {props.show ?
                <div className="modal-background fade-in">

                    <div className={`${props.size === "large" ? props.size : `default`}-modal `}>

                        <div className="header-modal" >
                            <p className="tittle-modal">{props.tittle}</p>
                            <C_Icon style={{ position: "absolute", right: 0, top: 15,color:"gray"}} onClick={closeModal} icon="close" />
                        </div>

                        <div className="body-modal">
                           {props.children}
                        </div>
                        
                        <div className="footer-modal">
                            <C_ButtonIcon themeType="outline" onClick={closeModal} label="Cancelar" />
                            <C_ButtonIcon style={{marginLeft:10}} label="Cadastrar" />
                        </div>
                        

                    </div>

                </div>
            : undefined}
        </>
    )
}

export function C_TextField(props){
    
    var style = props.style ? props.style : {};

    return (
        <div style={{display:"flex",alignItems:"center"}}>
            <FontIcon style={{marginRight:"10px"}}>{props.icon}</FontIcon>

            <TextField
                theme={"outline"}
                label={props.label}
                placeholder={props.placeholder}
                value={props.value}
                style={style}

            />
        </div>
    )
}

export function C_SelectField(props){
    
    var style = props.style ? props.style : {};

    return (
        <div style={{display:"flex",alignItems:"center"}}>
            <FontIcon style={{marginRight:"10px"}}>{props.icon}</FontIcon>

            <Select
                theme={"outline"}
                label={props.label}
                placeholder={props.placeholder}
                value={props.value}
                style={style}
                value={props.value}
                onChange={props.onChange}
                options={["left", "center", "right"]}

            />
        </div>
    )
}

export function C_SelectVehicle(props){
    
    const vehicleOptions = [
        { id: 1, label: "Carro" },
        { id: 2, label: "Moto" },
        { id: 3, label: "Bicicleta" }
    ];

    const [idVehicle, setIdVehicle] = useState({ id: 1, label: "Carro" });
    

    var style = props.style ? props.style : {};

    function onChange(undefined,item){
        console.log("item", item);

        setIdVehicle(item)
        if(props.onChange)
            props.onChange(item);
    }

    return (
        <div style={{display:"flex",alignItems:"center"}}>
            <span style={{ color: "#757575",marginRight: "10px",fontSize:24}}>
                <FaCarSide />
            </span>

            <Select
                {...props}
                theme={"outline"}
                label={"Veiculo"}
                style={style}
                onChange={onChange}
                options={vehicleOptions}
                value={idVehicle}
                key={"id"}
            />
          
        </div>
    )
}
export function C_SelectVehicleBrand(props){
    
    var style = props.style ? props.style : {};

    return (
        <div style={{display:"flex",alignItems:"center"}}>

            <Select
                theme={"outline"}
                label={"Marca"}
                style={style}
                onChange={props.onChange}
                options={["Alfa Romeo", "Audi", "Bentley"]}

            />
        </div>
    )
}
