import React, { useState } from 'react';

import { Select} from "@react-md/form";
import { FontIcon } from "@react-md/icon";


import { FaCarSide } from 'react-icons/fa';

<FormControl variant="outlined" className={classes.formControl}>
    <InputLabel id="demo-simple-select-outlined-label">Age</InputLabel>
    <Select
        labelId="demo-simple-select-outlined-label"
        id="demo-simple-select-outlined"
        value={age}
        onChange={handleChange}
        label="Age"
    >
        <MenuItem value="">
            <em>None</em>
        </MenuItem>
        <MenuItem value={10}>Ten</MenuItem>
        <MenuItem value={20}>Twenty</MenuItem>
        <MenuItem value={30}>Thirty</MenuItem>
    </Select>
</FormControl>

export function C_SelectField(props){
    
    var style = props.style ? props.style : {};

    return (
        <div style={{display:"flex",alignItems:"center"}}>
            <FontIcon style={{marginRight:"10px"}}>{props.icon}</FontIcon>

            <Select
                
                theme={"outline"}
                label={props.label}
                placeholder={props.placeholder}
                value={props.value}
                style={style}
                value={props.value}
                onChange={props.onChange}
                options={["left", "center", "right"]}

            />
        </div>
    )
}

export function C_SelectVehicle(props){
    
    const vehicleOptions = ["Carro","Moto","Bicicleta"];

    const [vehicle, setVehicle] = useState("Carro");
    

    var style = props.style ? props.style : {};

    function onChange(undefined,item){

        setVehicle(item);
        if(props.onChange)
            props.onChange(item);
    }

    return (
        <div style={{display:"flex",alignItems:"center"}}>
            {vehicle == "Carro" ?
                <span style={{ color: "#757575",marginRight: "10px",fontSize:24}}>
                    <FaCarSide/> 
                </span>
            :
                <FontIcon style={{ marginRight: "10px" }}>
                    {vehicle == "Moto" ? "two_wheeler" : "pedal_bike"}
                </FontIcon>
            }
            

            <Select
                theme={"outline"}
                label={"Veiculo"}
                style={style}
                onChange={onChange}
                options={vehicleOptions}
                value={vehicle}
            />
          
        </div>
    )
}

export function C_SelectVehicleBrand(props){
    
    var style = props.style ? props.style : {};

    return (
        <div style={{display:"flex",alignItems:"center"}}>

            <Select
                theme={"outline"}
                label={"Marca"}
                style={style}
                onChange={props.onChange}
                options={["Alfa Romeo", "Audi", "Bentley"]}

            />
        </div>
    )
}
