const helper = {

    displayCPF: function (_v) {
        if (!_v) return "";

        var v = _v + "";

        v = v.replace(/\D/g, "").substr(0, 11);       //Remove tudo o que não é dígito
        v = v.replace(/(\d{3})(\d)/, "$1.$2");       //Coloca um ponto entre o terceiro e o quarto dígitos
        v = v.replace(/(\d{3})(\d)/, "$1.$2");       //Coloca um ponto entre o terceiro e o quarto dígitos
        //de novo (para o segundo bloco de números)
        v = v.replace(/(\d{3})(\d{1,2})$/, "$1-$2") //Coloca um hífen entre o terceiro e o quarto dígitos

        return v
    },

    Post(url,sendData,showLoading,cb){
        fetch(url, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(sendData)
        })
        .then((res) => res.json())
        .then((data) => {
            return cb(false,data)
        })
        .catch(function (err) {
            return cb(err)
        });
    },

    Get(url,sendData,showLoading,cb){

        var params = new URLSearchParams(sendData).toString();

        fetch(url + "?" + new URLSearchParams(sendData))
        .then((res) => res.json())
        .then((data) => {
            return cb(false,data)
        })
        .catch(function (err) {
            return cb(err)
        });
    },



}

export default helper;
