const helper = {

    Post(url,sendData,showLoading,cb){
        fetch(url, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(sendData)
        })
        .then((res) => res.json())
        .then((data) => {
            return cb(false,data)
        })
        .catch(function (err) {
            return cb(err)
        });
    },

    Get(url,sendData,showLoading,cb){

        Object.keys(sendData).forEach(key => url.searchParams.append(key, sendData[key]))
        console.log("");
        fetch(url)
        .then((res) => res.json())
        .then((data) => {
            return cb(false,data)
        })
        .catch(function (err) {
            return cb(err)
        });
    }



}

export default helper;
