import React, { useState, useEffect } from "react";
import './App.css';

import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import { teal, red } from '@material-ui/core/colors';

import {MenuBar} from './Components/core/menuBar/MenuBar';
import {MainPage} from './Components/pages/mainPage/MainPage';
import {Clients} from './Components/pages/clients/Clients';


export function App() {

  const [pageSelected, setpageSelected] = useState("MAINPAGE");
  const [device, setDevice] = useState("MONITOR");

  useEffect((err, data) => {

    var mql = window.matchMedia("(max-width: 550px)");

    if (mql.matches)
      setDevice("MOBILE");

  }, []);




  function onChangePage(pageName) {
    setpageSelected(pageName);
  }


  return(
    <ThemeProvider theme={theme}>
      <div id="App">
        <div style={{ display: device == "MONITOR" ? "flex" : "block"}}>

          {device == "MONITOR" ?
            <MenuBar device onClick={(pageName) => onChangePage(pageName)} />
          : undefined}

          <div style={{ width: `calc(100vw - ${device == "MONITOR" ? "60px" : "0px"})`, height: `calc(100vh - ${device == "MONITOR" ? "0px" : "50px"})`}}>

            {pageSelected === "MAINPAGE" ?
              <MainPage device />
            :
              (pageSelected === "CLIENTS" ?
                <Clients device/>
              : undefined)
            }

          </div>

          {device == "MOBILE" ?
            <MenuBar device onClick={(pageName) => onChangePage(pageName)} />
          : undefined}

        </div>
      </div> 
    </ThemeProvider>
  )
}
