import React, { useState, useEffect } from "react";
import './App.css';

import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import { teal, red } from '@material-ui/core/colors';

import {MenuBar} from './Components/core/menuBar/MenuBar';
import {MainPage} from './Components/pages/mainPage/MainPage';
import {Clients} from './Components/pages/clients/Clients';


export function App() {

  const theme = createMuiTheme({
    palette: {
      primary: {
        light: '#757ce8',
        main: teal[500],
        dark: '#002884',
        contrastText: '#fff',
      },
      secondary: {
        light: '#ff7961',
        main: red[500],
        dark: '#ba000d',
        contrastText: '#000',
      },
      page:{
        light:"white",
      },
    },
  });

  useEffect((err, data) => {
    if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {
      document.write("mobile device");
    } else {
      // false for not mobile device
      document.write("not mobile device");
    }
  }, []);

  const [pageSelected, setpageSelected] = useState("MAINPAGE");

  function onChangePage(pageName) {
    setpageSelected(pageName);
  }


  return(
    <ThemeProvider theme={theme}>
      <div id="App">
        <div style={{display:"flex"}}>
          <MenuBar onClick={(pageName) => onChangePage(pageName)}/>

          <div style={{ width: "calc(100vw - 60px)"}}>

            {pageSelected === "MAINPAGE" ?
              <>
                <MainPage />
              </>
            :
              (pageSelected === "CLIENTS" ?
                <Clients />
              : undefined)
            }

          </div>
        </div>
      </div> 
    </ThemeProvider>
  )
}
