import React from 'react';
import { C_Button, C_Icon} from './C_Button';

import "../CoreStyle.css";
import "./Modal.css";
import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    tittle: {
        fontSize: theme.fontSize.h1,
    },

}));

export function C_Modal(props) {
    const classes = useStyles();


    function closeModal() {
        if (props.onClose)
            props.onClose();
    }

    return (
        <>
            {props.show ?
                <div className="modal-background fade-in">

                    <div className={`${props.size === "large" ? props.size : `default`}-modal `}>

                        <div className="header-modal" >
                            <p className={classes.tittle}>{props.tittle}</p>
                            <C_Icon color="grey" onClick={closeModal} icon="close" />
                        </div>

                        <div className="body-modal">
                            {props.children}
                        </div>

                        <div className="footer-modal">
                            <C_Button variant="outlined" onClick={closeModal} label="Cancelar" />
                            <C_Button style={{ marginLeft: 10 }} label="Cadastrar" onClick={props.onSave} />
                        </div>


                    </div>

                </div>
            : undefined}
        </>
    )
}
