import React, { useState, useEffect } from 'react';

import "./CoreStyle.css";
import { Button } from "@react-md/button";
import { FontIcon, TextIconSpacing } from "@react-md/icon";
// import { MdAccountCircle } from 'react-icons/md';


export function C_Icon(props) {
   
    var style = props.style ? props.style : {};

    return (
        <Button 
            onClick={props.onClick}
            icon={props.icon} 
            aria-label="More Info" 
            buttonType="icon"
            theme={props.primary ? "primary" : props.secondary ? "secondary" : "primary"}
            style={style}
            >
            <FontIcon>{props.icon}</FontIcon>
        </Button>
    )
}

export function C_ButtonIcon(props) {
   
    var style = props.style ? props.style : {};

    return (
        <div>
            <Button 
                onClick={props.onClick}
                theme={props.primary ? "primary" : props.secondary ? "secondary" : "primary"}
                themeType="contained"
                style={style}
                >
                <TextIconSpacing icon={<FontIcon>{props.icon}</FontIcon>}>
                    {props.label}
                </TextIconSpacing>
            </Button>
        </div>
    )
}

export function C_Modal(props) {

    const [showModal,setShowModal] = useState(true);

    var style = props.style ? props.style : {};
    
    return (
        <>
            {props.show && showModal?
                <div style={{position:"fixed",top:0,left:0,width:"100%",height:"100%",background:"black",opacity:0.5,display:"flex",alignItems:"center",justifyContent:"center"}}> 

                    <div style={{width:"40%",height:"60%",background:"white"}}>

                        <header style={{ display: "flex", alignItems: "center", justifyContent: "space-betwen"}} className="fade-in">
                            <h1>{props.tittle}</h1>
                            <C_Icon onClick={setShowModal(false)} icon="close" />
                        </header>
                        <body>
                            
                        </body>

                    </div>

                </div>
            : undefined}
        </>
    )
}
