import React, { useState, useEffect } from 'react';

import '../../core/CoreStyle.css';
import { C_Button } from '../../core/Components/C_Button';
import { C_Modal } from '../../core/Components/C_Modal';
import { C_TextField, C_TextFieldCpfAndCnpj, C_TextFieldObs } from '../../core/Components/C_TextField';
import { C_SelectVehicle, C_SelectVehicleBrand } from '../../core/Components/C_SelectFields';
import helper from '../../core/Helper';
import { makeStyles } from '@material-ui/core';

import "../../core/CoreStyle.css";

const useStyles = makeStyles((theme) => ({
    div: {
        height: "60px",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "0px 10px 0px 10px",
    },

    page: {
        width: "100%",
        height: "100%",
        background: theme.palette.page.light,
    },

    tittle: {
        fontSize: theme.fontSize.h1,
    },

    wrapFields: { width: "100%", alignItems: "center", marginBottom: "28px" },

}));

export function Clients(props) {
    const classes = useStyles();

    const [showModalClient, setShowModalClient] = useState(false);
    const [showCarFields, setShowCarFields] = useState(false);

    useEffect((err, data) => {
        // helper.Get("/api/client/list", { get: "success", secondTest: 1 }, false, (err, ret) => {
        //     console.log("err", err);
        //     console.log("ret", ret);
        // });
    }, []);

    function showModal() {
        setShowModalClient(true);
    }

    function verifyOnOk() {
        if (showCarFields)
            registerClient();
        else
            setShowCarFields(true);
    }

    function registerClient() {
        // helper.Post("/api/client/create", { Name: "the first", age: 18, state: "SC" }, false, (err, ret) => {
        //     console.log("err", err);
        //     console.log("ret", ret);
        // });
    }

    return (
        <div className={classes.page}>

            <span className={`${classes.div} fade-in`}>
                <p className={classes.tittle}>Clientes</p>
                <C_Button onClick={showModal} icon="person_add" label="Cadastrar" />
            </span>

            <C_Modal onOkLabel={showCarFields ? "Cadastrar" : "Proximo"} onOk={verifyOnOk} onCancel={() => { setShowModalClient(false); setShowCarFields(false)}} backProperty={showCarFields} onBack={() => setShowCarFields(false)} tittle={showCarFields ? "Dados do Automovel" : "Dados do Cliente"} show={showModalClient}>

                {!showCarFields ? 
                    <div className="fade-in">
                     
                        <div className={classes.wrapFields} style={{ display: props.device == "MOBILE" ? "block" : "flex" }}>
                            <div style={{ width: props.device == "MOBILE" ? "100%" : "59%", marginRight: props.device == "MOBILE" ? undefined : "2%", marginBottom: props.device == "MOBILE" ? "20px" : undefined }}>
                                <C_TextField outoFocus={true} style={{ width: "100%" }} label="Nome Completo" icon="person" />
                            </div>
                            <div style={{ width: props.device == "MOBILE" ? "100%" : "39%" }}>
                                <C_TextFieldCpfAndCnpj style={{ width: "100%" }} />
                            </div>
                        </div>

                        <div className={classes.wrapFields} style={{ display: props.device == "MOBILE" ? "block" : "flex" }}>
                            <div style={{ width: props.device == "MOBILE" ? "100%" : "39%", marginRight: props.device == "MOBILE" ? undefined : "2%", marginBottom: props.device == "MOBILE" ? "20px" : undefined }}>
                                <C_TextField style={{ width: "100%" }} label="Celular" icon="phone_iphone" />
                            </div>
                            <div style={{ width: props.device == "MOBILE" ? "100%" : "59%" }}>
                                <C_TextField style={{ width: "100%" }} label="Email" icon="email"/>
                            </div>
                        </div>

                        <div style={{ width: "100%" }}>
                            <C_TextFiel style={{ width: "100%" }} />
                        </div>
                    </div>
              
                    :
                    <div className="fade-in">

                        <div className={classes.wrapFields} style={{ display: props.device == "MOBILE" ? "block" : "flex" }}>
                            <div style={{ width: props.device == "MOBILE" ? "100%" : "49%", marginRight: props.device == "MOBILE" ? undefined : "2%", marginBottom: props.device == "MOBILE" ? "20px" : undefined }}>
                                <C_SelectVehicle style={{ width: "100%" }} />
                            </div>
                            <div style={{ width: props.device == "MOBILE" ? "100%" : "49%" }}>
                                <C_SelectVehicleBrand style={{ width: "100%" }} />
                            </div>
                        </div>

                        <div className={classes.wrapFields} style={{ display: props.device == "MOBILE" ? "block" : "flex" }}>
                            <div style={{ width: props.device == "MOBILE" ? "100%" : "49%", marginRight: props.device == "MOBILE" ? undefined : "2%", marginBottom: props.device == "MOBILE" ? "20px" : undefined }}>
                                <C_TextField style={{ width: "100%" }} icon="commute" label="Modelo" />
                            </div>
                            <div style={{ display:"flex",width: props.device == "MOBILE" ? "100%" : "49%" }}>
                                <div style={{ width: "39%", marginRight:"2%"}}>
                                    <C_TextField icon="date_range" style={{ width: "100%" }} label="Ano" />
                                </div>
                                <div style={{ width: "59%" }}>
                                    <C_TextField icon="speed" style={{ width: "100%" }} label="Kilometragem" />
                                </div>
                            </div>
                        </div>

                        <div style={{width: "100%"}}>
                            <C_TextFieldObs style={{ width: "100%" }}/>
                        </div>

                    </div>
                }

            </C_Modal>

        </div>
    );
}
