import React, { useState, useEffect } from 'react';

import "../CoreStyle.css";
import "./ComponentsStyle.css";
import { Button } from "@react-md/button";
import { FormMessage, TextField } from "@react-md/form";
import { FontIcon, TextIconSpacing } from "@react-md/icon";
// import { MdAccountCircle } from 'react-icons/md';


export function C_Icon(props) {
   
    var style = props.style ? props.style : {};

    return (
        <Button 
            onClick={props.onClick}
            icon={props.icon} 
            aria-label="More Info" 
            buttonType="icon"
            theme={props.theme ? props.theme : props.primary ? "primary" : props.secondary ? "secondary" : "primary"}
            style={style}
            >
            <FontIcon>{props.icon}</FontIcon>
        </Button>
    )
}

export function C_ButtonIcon(props) {
   
    var style = props.style ? props.style : {};

    return (
        <div>
            <Button 
                onClick={props.onClick}
                theme={props.theme ? props.theme : props.primary ? "primary" : props.secondary ? "secondary" : "primary"}
                themeType={props.themeType ? props.themeType : "contained"}
                style={style}
                >
                {props.icon ? 
                    <TextIconSpacing icon={<FontIcon>{props.icon}</FontIcon>}>
                        {props.label}
                    </TextIconSpacing>
                :
                    <>{props.label}</>
                }
            </Button>
        </div>
    )
}

export function C_Modal(props) {


    function closeModal(){
        if (props.onClose)
            props.onClose();
    }

    return (
        <>
            {props.show ?
                <div className="modal-background fade-in">

                    <div className={`${props.size === "large" ? props.size : `default`}-modal `}>

                        <div className="header-modal" >
                            <p className="tittle-modal">{props.tittle}</p>
                            <C_Icon style={{color:"gray"}} onClick={closeModal} icon="close" />
                        </div>

                        <div className="body-modal">
                           {props.children}
                        </div>
                        
                        <div className="footer-modal">
                            <C_ButtonIcon themeType="outline" onClick={closeModal} label="Cancelar" />
                            <C_ButtonIcon style={{marginLeft:10}} label="Cadastrar" />
                        </div>
                        

                    </div>

                </div>
            : undefined}
        </>
    )
}

export function C_TextField(props){
    
    var style = props.style ? props.style : {};

    return (
        <div style={{display:"flex",alignItems:"center"}}>
            <FontIcon style={{marginRight:"10px"}}>{props.icon}</FontIcon>

            <TextField
                theme={"outline"}
                label={props.label}
                placeholder={props.placeholder}
                value={props.value}
                style={style}
            />

        </div>
    )
}
